mapper.select:
        SELECT
		result_choice_id,
		score,
		question,
		a,
		b,
		c,
		d,
		correct,
		target,
		create_date,
		update_date,
		result_reading_id,
		result_id
	FROM result_choice_tb


			AND  result_reading_id=#{resultReadingId}


			AND  result_id=#{resultId}


			AND  correct=#{correct}

		AND ${orderName}

		>=


		  <= 

		(SELECT ${orderName} FROM result_choice_tb


					AND  result_reading_id=#{resultReadingId}


					AND  result_id=#{resultId}


					AND  correct=#{correct}


			ORDER BY
				${orderName}  ${orderWay}
			LIMIT #{pageNum}, 1
			)

		ORDER BY
			${orderName}  ${orderWay}
		LIMIT
			#{pageSize}

SELECT
 	count(result_choice_id)
FROM result_choice_tb


		AND  result_reading_id=#{resultReadingId}


		AND  result_id=#{resultId}


		AND  correct=#{correct}
